#!/usr/bin/env node

"use strict"





const doc = `
Name:
	pguests - Monitor IPs connecting to a machine.

Usage:
	pguests [-i IFACE | -interface IFACE]
	pguests (-h | --help | --version)

Description:

Options:
	-i IFACE, -interface IFACE    The interface on which to capture packets.
	-h, --help                    Display this documentation.
`





const docopt  = require('docopt').docopt
const pguests = require('pguests/app/pguests.js')

require('babel-polyfill')





const args    = docopt(doc)





pguests(args)
